
public class IncrementOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int number=10;
         number=number+1;
         System.out.println(number);
         number=number+number;
         System.out.println(number);
         
         number++;// increments the value by one
         System.out.println(number);
         System.out.println("**********");
         
         
	}

}
