package ReviewClass02;

public class ArithmeticOps {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		char a='A';
		char b= 'B';
		
	System.out.println(a+b);
	System.out.println(a-b);
	System.out.println(a*b);
	System.out.println(a/b);
	System.out.println(a%b);
	}

}
